{call MANAGER.MANAGER_GET_PRODUCTS(
        #{memberId, mode=IN, jdbcType=VARCHAR},
        #{result, mode=OUT, jdbcType=CURSOR, javaType=ResultSet, resultMap=managerProductMap}
      )}

