mapper.select:
UPDATE tbl_member
SET REFRESH_TOKEN = #{refreshToken}
WHERE member_id = #{memberId}

mapper.update:
UPDATE tbl_member
SET member_nickname = #{nickname, jdbcType=VARCHAR}
  , member_image = #{imgUrl, jdbcType=VARCHAR}
  , modified_at = sysdate
WHERE member_id = #{id}

UPDATE tbl_member
SET deleted_at = null
  , member_nickname = #{nickname, jdbcType=VARCHAR}
  , modified_at = sysdate
WHERE member_id = #{id}
